import React, { Component } from 'react'
import TextInputBox from './TextInputBox'
import ScoreBar from './ScoreBar'
import './css/App.css'

class App extends Component{

    constructor(props){
        super(props)
        this.state = {
            score : 60
        }
    }

    updateScore = (value) => {
        console.log('called')
        this.setState({
            score : this.state.score + 100
        })
    }

    render(){
        return(
            <div className="form-review">
                <h1 className="h3 mb-3 font-weight-normal">What do you think of the website?</h1>
                <TextInputBox callback={this.updateScore}/>
                <h5>Positivity Score</h5>
                <ScoreBar value = {this.state.score}/>
                <button className="btn btn-lg btn-primary btn-block" id="submit_button">
                    <span>Submit</span>
                    <i className="fa fa-paper-plane m-l-7"></i>
                </button>
            </div>
        )
    }
}

export default App
