import React, { Component } from 'react'
import './css/ScoreBar.css'

class TextInputBox extends Component{

    constructor(props){
        super(props)
        this.state = {
            value : ""
        }
    }

    render(){
        return(
            <textarea className="review" rows="8" placeholder="Write something here..."></textarea>
        )
    }
}

export default TextInputBox
